    private void btn_depositActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_depositActionPerformed
        if (list.isExist(cnic1.getText()))
        {
            try{
                double amount = Double.parseDouble(deposit1.getText());
                
                if(amount<0){
                    JOptionPane.showMessageDialog(null,"Jumlah tidak boleh negatif");
                    return ;
                }
                dataDis.setText(list.Deposit(cnic1.getText(),amount));
                dataDis.setText(list.cnicInfo(cnic1.getText()));
                
                writeFile(list.alldata());
                JOptionPane.showMessageDialog(null,"Update Berhasil !!");
            } catch (Exception e){
                JOptionPane.showMessageDialog(null,"Masukkan jumlah dengan benar");
            }
        }
        else 
        {
            JOptionPane.showMessageDialog(null,"CNIC tidak ada");
            dataDis.setText("");
        }
    }//GEN-LAST:event_btn_depositActionPerformed

    private void btn_infoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_infoActionPerformed
        if (list.isExist(cnic1.getText()))
        {
            dataDis.setText(list.cnicInfo(cnic1.getText()));
        }
        else {
            JOptionPane.showMessageDialog(null,"CNIC tidak ada");
        }
    }//GEN-LAST:event_btn_infoActionPerformed

    private void btn_registerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_registerActionPerformed
        if (list.isExist(cnic.getText())){
            JOptionPane.showMessageDialog(null,"CNIC sudah ada");
        }
        else if (cnic.getText().equals("")||name.getText().equals("")||deposit.getText().equals("")){
            JOptionPane.showMessageDialog(null,"Harus Diisi Semua");
        }
        else if (checkCNIC(cnic.getText())){
            Random rand = new Random();
            int pin = (int) Math.floor(Math.random()*(9999-1000+1)+1000);
            int c1,c2,c3,c4;
            do{
                c1 = (int) Math.floor(Math.random()*(9999-1000+1)+1000);
                c2 = (int) Math.floor(Math.random()*(9999-1000+1)+1000);
                c3 = (int) Math.floor(Math.random()*(9999-1000+1)+1000);
                c4 = (int) Math.floor(Math.random()*(9999-1000+1)+1000);
            } while (list.check(c1+" "+c2+" "+c3+" "+c4));
        
        int a1 = (int) Math.floor(Math.random()*(999999-100000+1)+100000);
        int a2 = (int) Math.floor(Math.random()*(999999-100000+1)+100000);
        
        try{
            double amount = Double.parseDouble(deposit.getText());
            if (amount<0){
                JOptionPane.showMessageDialog(null,"Jumlah tidak boleh negatif");
                return ;
            }
            
            list.insert(new Customer(cnic.getText(),name.getText(),amount,pin,"PK00COMSATS0"+a1+""+a2,c1+" "+c2+" "+c3+" "+c4));
            
            writeFile(list.alldata());
            JOptionPane.showMessageDialog(null,"Registrasi Customer Sukses! \nDengan Account Number ="+"PK00COMSATS0"+a1+""+a2);
            name.setText("");
            cnic.setText("");
            deposit.setText("");
        } 
        catch (Exception e)
        {
            JOptionPane.showMessageDialog(null,"Masukkan jumlah yang benar !");
        }
        }
        else{
                JOptionPane.showMessageDialog(null,"CNIC harus berjumlah 13 digit");
                }
    }//GEN-LAST:event_btn_registerActionPerformed

