    private void writeFile(String data){
        try{
            FileWriter myWriter = new FileWriter("D:\\TugasGui\\data.txt");
            myWriter.write(data);
            myWriter.close();
            System.out.println("Sukses menulis pada file");
        } catch (IOException e){
            System.out.println("Ada error");
            e.printStackTrace();
        }
    }
    
    private boolean checkCNIC (String text){
        if (text.length()==13){
            boolean ret = false;
            for (int i=0;i<text.length();i++){
                if (text.charAt(i)>=48&&text.charAt(i)<=57){
                    ret=true;
                }
                else 
                {
                    ret = false;
                    break;
                }
            } return ret;
        }
        else {
            return false;
        }
    }
    
    private void readFile(){
        try{
            File myObj = new File ("D:\\TugasGui\\data.txt");
            Scanner myReader = new Scanner (myObj);
            while (myReader.hasNextLine()){
                String data = myReader.nextLine();
                if (data.length()>5){
                    String[] cus = data.split(";");
                    list.insert (new Customer(cus[0],cus[1],Double.parseDouble(cus[2]),Integer.parseInt(cus[3]),cus[4],cus[5]));
                }
            } myReader.close();
        } catch (FileNotFoundException e){
            System.out.println("ada error");
            e.printStackTrace();
        }
    }
    
    private void checkFileName() {
        try {
            File myObj = new File ("D:\\TugasGui\\data.txt");
            if (myObj.createNewFile()){
                System.out.println("File Created : "+myObj.getName());
            }
            else {
                System.out.println("File already exists.");
            }
        } catch (IOException e){
            System.out.println("Ada error");
            e.printStackTrace();
        }
    }
    
    
